import React, { useState } from 'react';
import Card from 'react-bootstrap/Card';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import Accordion from 'react-bootstrap/Accordion';
import { cartItems } from './Cards'



const Cart = () => {
    const isEmpty = (cart) => {
        return (cart.length === 0) ? 'Your Cart is Empty ' : 'Cart Items' + ' (' + cart.length + ') '
    }

    function Cart() {
        const {
            isEmpty,
            cartTotal,
            totalUniqueItems,
            items,
            updateItemQuantity,
            removeItem,
            emptyCart,
            metadata
        } = useCart();

        if (isEmpty) return <p>Your cart is empty</p>;

        return (
            <>
                <h1>
                    Cart ({totalUniqueItems} - {cartTotal})
                </h1>

                <pre>{JSON.stringify(metadata, null, 2)}</pre>

                {!isEmpty && <button onClick={emptyCart}>Empty cart</button>}

                <ul>
                    {items.map((item) => (
                        <li key={item.id}>
                            {item.quantity} x {item.name}
                            <button
                                onClick={() => updateItemQuantity(item.id, item.quantity - 1)}
                            >
                                -
                            </button>
                            <button
                                onClick={() => updateItemQuantity(item.id, item.quantity + 1)}
                            >
                                +
                            </button>
                            <button onClick={() => removeItem(item.id)}>Remove &times;</button>
                        </li>
                    ))}
                </ul>
            </>
        );
    }

    const renderCart = () => (
        <>
            <h3 className='text-center py-4' id='fashion'>{isEmpty(cartItems)}<img src={require('../assets/cart.png')} height='36' /></h3>
            <Row xs={4} md={4} className="g-4">

                {cartItems.map((product) => (

                    <Col>
                        <Card>
                            {product.image}
                            <Card.Body>
                                <Card.Title>{product.title}</Card.Title>
                                <Card.Subtitle>Quantity: {product.quantity}</Card.Subtitle>
                                <h1 className='price'>{product.price}</h1>
                                <Accordion>
                                    <Accordion.Header>Description</Accordion.Header>
                                    <Accordion.Body>{product.description}</Accordion.Body>
                                </Accordion>
                            </Card.Body>

                        </Card>
                    </Col>
                ))}
            </Row>
        </>
    )

    return (
        <div>
            {renderCart()}
        </div>


    )
}

export default Cart
