import React, { useState } from 'react'
import Card from 'react-bootstrap/Card';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import Accordion from 'react-bootstrap/Accordion';
//export var cartItems = new Array();
var item = {};

export const PAGE_PRODUCTS = 'products'
export const PAGE_CART = 'cart'


function Cards() {
    
    const navigateTo = (page) => {
        setPage(page)
    }

    const [cart, setCart] = useState([])
    const [page, setPage] = useState('products')

    //categories
    const fashion = 'fashion'
    const tech = 'tech'
    const trending = 'trending'


    const [products] = useState([
        {
            id: 1,
            inCart: 0,
            quantity: 0,
            category: fashion,
            image: <img variant='top' src={require('../assets/item1.jpg')} />,
            title: 'Nike Hat1',
            price: '$40',
            description: 'Personalized Comfort. The Nike Sportswear Pro adjustable cap sports a classic six-panel design and press-on back for a personalized fit and Comfort'

        },
        {
            id: 1,
            inCart: 0,
            quantity: 0,
            category: fashion,
            image: <img variant='top' src={require('../assets/item1.jpg')} />,
            title: 'Nike Hat2',
            price: '$40',
            description: 'Personalized Comfort. The Nike Sportswear Pro adjustable cap sports a classic six-panel design and press-on back for a personalized fit and Comfort'

        },
        {
            id: 1,
            inCart: 0,
            quantity: 0,
            category: fashion,
            image: <img variant='top' src={require('../assets/item1.jpg')} />,
            title: 'Nike Hat3',
            price: '$40',
            description: 'Personalized Comfort. The Nike Sportswear Pro adjustable cap sports a classic six-panel design and press-on back for a personalized fit and Comfort'

        },
        {
            id: 1,
            inCart: 0,
            quantity: 0,
            category: fashion,
            image: <img variant='top' src={require('../assets/item1.jpg')} />,
            title: 'Nike Hat4',
            price: '$40',
            description: 'Personalized Comfort. The Nike Sportswear Pro adjustable cap sports a classic six-panel design and press-on back for a personalized fit and Comfort'

        },
        {
            id: 1,
            inCart: 0,
            quantity: 0,
            category: tech,
            image: <img variant='top' src={require('../assets/item1.jpg')} />,
            title: 'Nike Hat5',
            price: '$40',
            description: 'Personalized Comfort. The Nike Sportswear Pro adjustable cap sports a classic six-panel design and press-on back for a personalized fit and Comfort'

        },
        {
            id: 1,
            inCart: 0,
            quantity: 0,
            category: tech,
            image: <img variant='top' src={require('../assets/item1.jpg')} />,
            title: 'Nike Hat6',
            price: '$40',
            description: 'Personalized Comfort. The Nike Sportswear Pro adjustable cap sports a classic six-panel design and press-on back for a personalized fit and Comfort'

        },
        {
            id: 1,
            inCart: 0,
            quantity: 0,
            category: trending,
            image: <img variant='top' src={require('../assets/item1.jpg')} />,
            title: 'Nike Hat7',
            price: '$40',
            description: 'Personalized Comfort. The Nike Sportswear Pro adjustable cap sports a classic six-panel design and press-on back for a personalized fit and Comfort'

        },
        {
            id: 1,
            inCart: 0,
            quantity: 0,
            category: trending,
            image: <img variant='top' src={require('../assets/item1.jpg')} />,
            title: 'Nike Hat8',
            price: '$40',
            description: 'Personalized Comfort. The Nike Sportswear Pro adjustable cap sports a classic six-panel design and press-on back for a personalized fit and Comfort'

        }

    ]);

    const addToCart = (product) => {
        // console.log(product.inCart + " before")
        // product.inCart = 1
        // console.log(product.inCart + " after")
        setCart([...cart, product])


    }



    // const fashionProd = []
    // const techProd = []
    // const trendingProd = []


    // products.map(prod => {
    //     if (prod.category === fashion)
    //         return fashionProd.push(prod)
    //     if (prod.category === tech)
    //         return techProd.push(prod)
    //     if (prod.category === trending)
    //         return trendingProd.push(prod)
    // })



    // products.forEach(prod => {
    //     if (prod.inCart === 1) {
    //         !([cart, setCart].includes(prod)) ? [cart, setCart].push(prod): [cart, setCart][[cart, setCart].indexOf(prod)].quantity++  
    //     }
    //    console.log([cart, setCart].length)
    // })

    const isEmpty = (cart) => {
        return (cart.length === 0) ? 'Your Cart is Empty ' : 'Cart Items' + ' (' + cart.length + ') '
    }

    const renderProducts = () => (
       <>
        <h3 className='text-center py-4' id='fashion'>Products</h3>
            <Row xs={4} md={4} className="g-4">

                {products.map((product) => (

                    <Col className='products'>
                        <Card>
                            {product.image}
                            <Card.Body>
                                <Card.Title>{product.title}</Card.Title>
                                <h1 className='price'>{product.price}</h1>
                                <Accordion>
                                    <Accordion.Header>Description</Accordion.Header>
                                    <Accordion.Body>{product.description}</Accordion.Body>
                                </Accordion>
                            </Card.Body>
                            <button onClick={() => { addToCart(product) }} className="btn btn-clear ms-2" >Add to Cart<img src={require("../assets/cart.png")} height='33px' /></button>
                        </Card>
                    </Col>
                ))}
            </Row>
            </>
    )
    const renderCart = () =>(
        <>
                <h3 className='text-center py-4' id='fashion'>{isEmpty([cart, setCart])}<img src={require('../assets/cart.png')} height='36' /></h3>
                <Row xs={4} md={4} className="g-4">

                    {cart.map((product, idx) => (

                        <Col>
                            <Card key={idx}>
                                {product.image}
                                <Card.Body>
                                    <Card.Title>{product.title}</Card.Title>
                                    <Card.Subtitle>Quantity: {product.quantity}</Card.Subtitle>
                                    <h1 className='price'>{product.price}</h1>
                                    <Accordion>
                                        <Accordion.Header>Description</Accordion.Header>
                                        <Accordion.Body>{product.description}</Accordion.Body>
                                    </Accordion>
                                </Card.Body>

                            </Card>
                        </Col>
                    ))}
                </Row>
            </>
    )
    return (
        <div id='home'>

            
            <nav class="navbar navbar-expand-lg">
                <div class="container">
                    <a class="navbar-brand fw-bold fs-4" href="#home"><img src={require("../assets/logo.png")} height='34px' /></a>
                    <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
                        <span class="navbar-toggler-icon"></span>
                    </button>
                    <div class="collapse navbar-collapse" id="navbarSupportedContent">
                        <ul class="navbar-nav me-auto mb-2 mb-lg-0">
                            <li class="nav-item">
                                <a class="nav-link active" aria-current="page" href="#home">Home</a>
                            </li>
                            <li class="nav-item">
                                <a class="nav-link" href="#fashion">Fashion</a>
                            </li>
                            <li class="nav-item">
                                <a class="nav-link" href="#tech">Tech</a>
                            </li>
                            <li class="nav-item">
                                <a class="nav-link" href="#trending">Trending</a>
                            </li>

                        </ul>
                        <form class="d-flex" role="search">
                            <input class="form-control me-2 bg-light text-dark border-0" type="search" placeholder="Search for anything..." aria-label="Search" />
                            <button class="btn btn-clear" type="submit"><img src={require('../assets/search.png')} height='30' /></button>
                        </form>
                        <div className="buttons">
                            <a href="#login" className="btn btn-clear ms-2" >
                                <img src={require("../assets/login.png")} height='33px' /></a>

                            <button  onClick={() => navigateTo(PAGE_CART)} href='#cart' className="btn btn-clear ms-2">
                                <img src={require("../assets/cart.png")} height='33px' /></button>
                        </div>

                    </div>
                </div>
            </nav>
       

            {page === PAGE_PRODUCTS && renderProducts()}
            {page === PAGE_CART && renderCart()}

        </div>

    );
}

export default Cards;
