import React, { useState } from 'react'
import Card from 'react-bootstrap/Card';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import Accordion from 'react-bootstrap/Accordion';
import ReactDOM from "react-dom";
import { CartProvider, useCart } from "react-use-cart";

export var cartItems = new Array();
var item = {};



function Cards() {
    //categories
    const fashion = 'fashion'
    const tech = 'tech'
    const trending = 'trending'


    const [products] = useState([
        {
            id: 1,
            inCart: 0,
            quantity: 0,
            category: fashion,
            image: <img variant='top' src={require('../assets/item1.jpg')} />,
            title: 'Nike Hat1',
            price: '$40',
            description: 'Personalized Comfort. The Nike Sportswear Pro adjustable cap sports a classic six-panel design and press-on back for a personalized fit and Comfort'

        },
        {
            id: 1,
            inCart: 0,
            quantity: 0,
            category: fashion,
            image: <img variant='top' src={require('../assets/item1.jpg')} />,
            title: 'Nike Hat2',
            price: '$40',
            description: 'Personalized Comfort. The Nike Sportswear Pro adjustable cap sports a classic six-panel design and press-on back for a personalized fit and Comfort'

        },
        {
            id: 1,
            inCart: 0,
            quantity: 0,
            category: fashion,
            image: <img variant='top' src={require('../assets/item1.jpg')} />,
            title: 'Nike Hat3',
            price: '$40',
            description: 'Personalized Comfort. The Nike Sportswear Pro adjustable cap sports a classic six-panel design and press-on back for a personalized fit and Comfort'

        },
        {
            id: 1,
            inCart: 0,
            quantity: 0,
            category: fashion,
            image: <img variant='top' src={require('../assets/item1.jpg')} />,
            title: 'Nike Hat4',
            price: '$40',
            description: 'Personalized Comfort. The Nike Sportswear Pro adjustable cap sports a classic six-panel design and press-on back for a personalized fit and Comfort'

        },
        {
            id: 1,
            inCart: 0,
            quantity: 0,
            category: tech,
            image: <img variant='top' src={require('../assets/item1.jpg')} />,
            title: 'Nike Hat5',
            price: '$40',
            description: 'Personalized Comfort. The Nike Sportswear Pro adjustable cap sports a classic six-panel design and press-on back for a personalized fit and Comfort'

        },
        {
            id: 1,
            inCart: 0,
            quantity: 0,
            category: tech,
            image: <img variant='top' src={require('../assets/item1.jpg')} />,
            title: 'Nike Hat6',
            price: '$40',
            description: 'Personalized Comfort. The Nike Sportswear Pro adjustable cap sports a classic six-panel design and press-on back for a personalized fit and Comfort'

        },
        {
            id: 1,
            inCart: 0,
            quantity: 0,
            category: trending,
            image: <img variant='top' src={require('../assets/item1.jpg')} />,
            title: 'Nike Hat7',
            price: '$40',
            description: 'Personalized Comfort. The Nike Sportswear Pro adjustable cap sports a classic six-panel design and press-on back for a personalized fit and Comfort'

        },
        {
            id: 1,
            inCart: 0,
            quantity: 0,
            category: trending,
            image: <img variant='top' src={require('../assets/item1.jpg')} />,
            title: 'Nike Hat8',
            price: '$40',
            description: 'Personalized Comfort. The Nike Sportswear Pro adjustable cap sports a classic six-panel design and press-on back for a personalized fit and Comfort'

        }

    ]);

    function inCart(product) {
        console.log(product.inCart + " before")
        product.inCart = 1
        console.log(product.inCart + " after")


    }



    const fashionProd = []
    const techProd = []
    const trendingProd = []


    products.map(prod => {
        if (prod.category === fashion)
            return fashionProd.push(prod)
        if (prod.category === tech)
            return techProd.push(prod)
        if (prod.category === trending)
            return trendingProd.push(prod)
    })

    // products.map(prod => {
    //     if (prod.inCart === 1) {
    //         !(cartItems.includes(prod)) ? cartItems.push(prod) : cartItems[cartItems.indexOf(prod)].quantity++
    //     }
    //    console.log(cartItems.length)
    // })

    for (let i = 0; i < products.length; i++) {
        if (products[i].inCart === 1 && !(cartItems.includes(products[i]))) {
            cartItems.push(products[i])
        }
    }

    const renderProducts = (category, title) => (<>
        <h3 className='text-center py-4' id='fashion'>{title}</h3>
        <Row xs={4} md={4} className="g-4">

            {category.map((product) => (

                <Col className='products'>
                    <Card>
                        {product.image}
                        <Card.Body>
                            <Card.Title>{product.title}</Card.Title>
                            <h1 className='price'>{product.price}</h1>
                            <Accordion>
                                <Accordion.Header>Description</Accordion.Header>
                                <Accordion.Body>{product.description}</Accordion.Body>
                            </Accordion>
                        </Card.Body>
                        <button onClick={() => { inCart(product) }} className="btn btn-clear ms-2" >Add to Cart<img src={require("../assets/cart.png")} height='33px' /></button>
                    </Card>
                </Col>
            ))}
        </Row>
    </>)


    function Cart() {
        const {
            isEmpty,
            cartTotal,
            totalUniqueItems,
            items,
            updateItemQuantity,
            removeItem,
            emptyCart,
            metadata
        } = useCart();

        if (isEmpty) return <p>Your cart is empty</p>;

        return (
            <>
                <h1>
                    Cart ({totalUniqueItems} - {cartTotal})
                </h1>

                <pre>{JSON.stringify(metadata, null, 2)}</pre>

                {!isEmpty && <button onClick={emptyCart}>Empty cart</button>}

                <ul>
                    {items.map((item) => (
                        <li key={item.id}>
                            {item.quantity} x {item.name}
                            <button
                                onClick={() => updateItemQuantity(item.id, item.quantity - 1)}
                            >
                                -
                            </button>
                            <button
                                onClick={() => updateItemQuantity(item.id, item.quantity + 1)}
                            >
                                +
                            </button>
                            <button onClick={() => removeItem(item.id)}>Remove &times;</button>
                        </li>
                    ))}
                </ul>
            </>
        );
    }

    return (
        <div>
            {renderProducts(fashionProd, 'Fashion')}
            {renderProducts(techProd, 'Tech')}
            {renderProducts(trendingProd, 'Trending')}
        </div>

    );
}

export default Cards;
