import React, { useState } from 'react'
import Card from 'react-bootstrap/Card';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import Accordion from 'react-bootstrap/Accordion';
export var cartItems = new Array();
var item = {};



function Cards() {
    //categories
    const fashion = 'fashion'
    const tech = 'tech'
    const trending = 'trending'


    const [products] = useState([
        {
            id: 1,
            inCart: 0,
            quantity: 0,
            category: fashion,
            image: <img variant='top' src={require('../assets/item1.jpg')} />,
            title: 'Nike Hat1',
            price: '$40',
            description: 'Personalized Comfort. The Nike Sportswear Pro adjustable cap sports a classic six-panel design and press-on back for a personalized fit and Comfort'

        },
        {
            id: 1,
            inCart: 0,
            quantity: 0,
            category: fashion,
            image: <img variant='top' src={require('../assets/item1.jpg')} />,
            title: 'Nike Hat2',
            price: '$40',
            description: 'Personalized Comfort. The Nike Sportswear Pro adjustable cap sports a classic six-panel design and press-on back for a personalized fit and Comfort'

        },
        {
            id: 1,
            inCart: 0,
            quantity: 0,
            category: fashion,
            image: <img variant='top' src={require('../assets/item1.jpg')} />,
            title: 'Nike Hat3',
            price: '$40',
            description: 'Personalized Comfort. The Nike Sportswear Pro adjustable cap sports a classic six-panel design and press-on back for a personalized fit and Comfort'

        },
        {
            id: 1,
            inCart: 0,
            quantity: 0,
            category: fashion,
            image: <img variant='top' src={require('../assets/item1.jpg')} />,
            title: 'Nike Hat4',
            price: '$40',
            description: 'Personalized Comfort. The Nike Sportswear Pro adjustable cap sports a classic six-panel design and press-on back for a personalized fit and Comfort'

        },
        {
            id: 1,
            inCart: 0,
            quantity: 0,
            category: tech,
            image: <img variant='top' src={require('../assets/item1.jpg')} />,
            title: 'Nike Hat5',
            price: '$40',
            description: 'Personalized Comfort. The Nike Sportswear Pro adjustable cap sports a classic six-panel design and press-on back for a personalized fit and Comfort'

        },
        {
            id: 1,
            inCart: 0,
            quantity: 0,
            category: tech,
            image: <img variant='top' src={require('../assets/item1.jpg')} />,
            title: 'Nike Hat6',
            price: '$40',
            description: 'Personalized Comfort. The Nike Sportswear Pro adjustable cap sports a classic six-panel design and press-on back for a personalized fit and Comfort'

        },
        {
            id: 1,
            inCart: 0,
            quantity: 0,
            category: trending,
            image: <img variant='top' src={require('../assets/item1.jpg')} />,
            title: 'Nike Hat7',
            price: '$40',
            description: 'Personalized Comfort. The Nike Sportswear Pro adjustable cap sports a classic six-panel design and press-on back for a personalized fit and Comfort'

        },
        {
            id: 1,
            inCart: 0,
            quantity: 0,
            category: trending,
            image: <img variant='top' src={require('../assets/item1.jpg')} />,
            title: 'Nike Hat8',
            price: '$40',
            description: 'Personalized Comfort. The Nike Sportswear Pro adjustable cap sports a classic six-panel design and press-on back for a personalized fit and Comfort'

        }

    ]);

    function inCart(product) {
        console.log(product.inCart + " before")
        product.inCart = 1
        console.log(product.inCart + " after")


    }



    const fashionProd = []
    const techProd = []
    const trendingProd = []


    products.map(prod => {
        if (prod.category === fashion)
            return fashionProd.push(prod)
        if (prod.category === tech)
            return techProd.push(prod)
        if (prod.category === trending)
            return trendingProd.push(prod)
    })


    
    products.forEach(prod => {
        if (prod.inCart === 1) {
            !(cartItems.includes(prod)) ? cartItems.push(prod): cartItems[cartItems.indexOf(prod)].quantity++  
        }
       console.log(cartItems.length)
    })

    

    return (
        <div>

           {renderProducts()}

        </div>

    );
}

export default Cards;
